function getTotalElements(selector) {
	if (queryPermission('access_globals', 'read', 'nantuGetTotalElements')) {
		const nantuGetTotalElements = copyFromWindow('nantuGetTotalElements');

		return nantuGetTotalElements(selector);
	}

	return 0;
